package me.luey.adventofcode.day6;

public class UniqueCharacters {

    public static void main(String[] args) {
        String input = "";

        int index = getIndexAfterFourUniqueCharacters(input);
        System.out.println(index);
    }

    private static int getIndexAfterFourUniqueCharacters(String input) {
        for (int i = 14; i < input.length(); i++) {
            long uniqueChars = input.substring(i - 14, i).chars().distinct().count();
            System.out.println(i + " : " + input.substring(i - 14, i));
            if (uniqueChars == 14) {
                return i;
            }
        }

        throw new IllegalArgumentException("No marker detected");
    }

}
